import {GET_ALL_CAMPAIGN} from '../actions/types';

const initailState = {
    campaigns : []
};

export default (state = initailState, action) => {
    const {type, payload} = action;

    switch(type){
        case GET_ALL_CAMPAING:
            return {
                ...state,
                campaigns : payload
            }
        default:
            return state;   
    }
}
