import React, {Component,useState} from 'react';
import { NavLink,Link } from 'react-router-dom';
import Header from '../layout/Header';
// import selfiimage from '../../../assets/images/selfi_images.jpg';
import selfiimage from '../../../src/assets/images/selfi_images.jpg';

const Login = () => {
    const [user, setUser] = useState({
        email, password
    });
    let name,value;

    const handleInputs = (e) =>{
        name = e.target.name;
        value = e.target.value;
        setUser({...user,[name]:value});
    }

    return(
        <div className="fullpage">
            <header>
                <Header />
            </header>    
        <div className="login-container formfields">
        <div className="col-md-12">
            <div className="row">
                <div className="col-md-6">
                    <div className="login-form">
                        <h1>Login</h1>
                        <p className="">Don't have account?<Link to="/register"><span>Create account here</span></Link></p>    
                        <form className="" action="">
                            <div className="form-group">
                                <input type="text" className="form-control" id="" name="email" placeholder="E-mail"></input>
                            </div>
                            <div className="form-group">
                                <input type="password" className="form-control" id="" name="password" placeholder="Password"></input>
                            </div>
                            <button type="submit" className="btn btn-primary">Login</button>
                        </form>    
                        <p>By creating an account, I agree with GIFâ€™s</p>
                        <p><Link to="/"><span className="pinkclr">Privacy Policy</span></Link> and <Link to="/"><span className="purpleclr">Terms of Service.</span></Link></p>
                    </div>    
                </div> 
                <div className="col-md-6">   
                    <div className="selfi-image">
                        <img src={selfiimage} alt="selfiimage" />
                    </div>    
                </div>    
            </div>    
        </div>    
        </div>    
        </div>    
    )
}

export default Login;
