import axios from 'axios';
import CircularJSON from 'circular-json';
// var CircularJSON = require('circular-json');
import {
	LOGIN_SUCCESS
} from './types';

export const loginUser = userdata => async dispatch =>{
    const config = { headers: { 'Authorization': 'Bearer ' + 'token generated from fiddler after running auth',
     'Accept': 'application/json',
     'Content-Type': 'application/json' } };
	const body = CircularJSON.stringify(userdata);
	console.log('asgsdgbody',body);
    try {
		const res = await axios.post('https://viddey-backend.herokuapp.com/api/v1/login', body, config);
		console.log('dvdsvdsvdsv',res);
		dispatch({
			type: LOGIN_SUCCESS,
			payload: res.data
		});
    } catch (error) {
		console.log('errorerror',error);
	}   
};
